//0-intern 1-extern
uint8_t ADS111X_VREF(uint8_t mode){
  uint8_t cmd[2];
  cmd[0] = 0b01000000;
  cmd[1] = ADS111X_RREG(0);
  cmd[1] = cmd[1]&0xFE;
  if (mode == 1)
  {
    cmd[1] = cmd[1] | 0x01;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    if((0x0C&ADS111X_RREG(0))==0x0C){
      return 1;
    }else{
      return 0;
    }
  }else{
	  HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
	  return 0;
  }
}

//nastavení kanálu mux
uint8_t ADS111X_MUX(uint8_t AINP, uint8_t AINN){
  uint8_t cmd [2];
  cmd[0] = 0b01000000;
  cmd[1] = ADS111X_RREG(0);
  cmd[1] = 0x1F&cmd[1];
  
  switch (AINP)
  {
  case AIN0:
    if (AINN == AIN1)
    {
      HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    }else if (AINN == AGND)
    {
      cmd[1] = 0x60|cmd[1]; 
      HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    }
      return ADS111X_RREG(0);;
    
    break;
  case AIN1:
    if (AINN == AIN2)
      {
        cmd[1] = 0x40|cmd[1]; 
        HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      }else if (AINN == AGND)
      {
        cmd[1] = 0x80|cmd[1]; 
        HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      }
      return ADS111X_RREG(0);;
    break;
  case AIN2:
    if (AINN == AIN3)
      {
        cmd[1] = 0x20|cmd[1]; 
        HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      }else if (AINN == AGND)
      {
        cmd[1] = 0x90|cmd[1]; 
        HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      }
      return ADS111X_RREG(0);
      
    break;
  case AIN3:
    if (AINN == AGND)
      {
        cmd[1] = 0xC0|cmd[1]; 
        HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      }
        return ADS111X_RREG(0);
    break;
  case calibration:
    cmd[1] = 0xE0|cmd[1];
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    return ADS111X_RREG(1);
    break;
  default:
    return ADS111X_RREG(0);
    break;
  }
}

//nastavení conversion modu 0-single shot 1-continuous
uint8_t ADS111X_CM(uint8_t mode){
  uint8_t cmd [2];
  cmd[0] = 0b01000000;
  cmd[1] = ADS111X_RREG(0);
  cmd[1] = 0xFD&cmd[1];
  if (mode == 1)
  {
    cmd[1] = cmd[1]|0x02;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    ADS111X_start();
    if((0x02&ADS111X_RREG(0))==0x02){
      return 1;
    }else{
      return 0;
    }
  }else{
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
      return 0;
  }
}

/*nastavení zisku*/
uint8_t ADS111X_GRAIN(uint8_t GRAIN)
{
  uint8_t cmd [2];
  cmd[0] = 0b01000000;
  cmd[1] = ADS111X_RREG(0);
  switch (GRAIN)
  {
  case 1:
    cmd[1] = cmd[1]&0xEF;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    break;
  case 4:
    cmd[1] = cmd[1]|0x10;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    break;
  default:
    break;
  }
  return ADS111X_RREG(0);
}

/*nastavení datarate/sample speed vrací DR povolené hoodnoty 20,90,330,1000 jinak default 20, chyba 0*/
uint16_t ADS111X_DR(uint16_t datarate){
  uint8_t cmd[2];
  cmd[0] = 0b01000000;
  cmd[1] = ADS111X_RREG(0);
  cmd[1] = cmd[1]&0xFC;
  switch (datarate)
  {
  case 90:
    cmd[1] = cmd[1] | 0x04;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    if((0x0C&ADS111X_RREG(0))==0x04){
      return 90;
    }else{
      return 0;
    }
    break;
  case 330:
    cmd[1] = cmd[1] | 0x08;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    if((0x0C&ADS111X_RREG(0))==0x08){
      return 330;
    }else{
      return 0;
    }
    break;
  case 1000:
    cmd[1] = cmd[1] | 0x0C;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    if((0x0C&ADS111X_RREG(0))==0x0C){
      return 1000;
    }else{
      return 0;
    }
    break;
  default:
  /*defalt 20sps*/
    //cmd[1] = cmd[1] | 0x00;
    HAL_I2C_Master_Transmit(&hi2c1, ADS111X_addr, cmd, 2 , 10);
    if((0x0C&ADS111X_RREG(0))==0x00){
      return 20;
    }else{
      return 0;
    }
    break;
  }
}

