void ADS111X_cal(void){
  int32_t tmp = 0; 
  ADS111X_MUX(calibration, calibration);
  ADS111X_CM(0);
  ADS111X_start();
  for (uint8_t i = 0; i < 10; i++)
  {
    while (ADS111X_ready() == 0)
    {
      
    }
    tmp += ADS111X_read();
  }
  tmp /= 10;
  offset = (int16_t)tmp;
}

